mapper.select:
select comment.id, comment.title, comment.content, comment.user_id as userId,comment.user_name as userName, comment.create_time as createTime, n.node_id as relatedId, 'node' as type
from comment,
    (select *
    from node_comment where node_id in
         (select id from node where project_id=#{projectId})) n
where comment.id = n.comment_id;

select comment.id, comment.title, comment.content, comment.user_id as userId,comment.user_name as userName, comment.create_time as createTime,n.edge_id as relatedId, 'edge' as type
from comment,
     (select *
      from edge_comment where edge_id in
                              (select id from edge where project_id=#{projectId})) n
where comment.id = n.comment_id;

select comment.id, comment.title, comment.content, comment.user_id as userId,comment.user_name as userName, comment.create_time as createTime,n.domain_id as relatedId, 'domain' as type
from comment,
     (select *
      from domain_comment where domain_id in
                              (select id from domain where project_id=#{projectId})) n
where comment.id = n.comment_id;
